import React from "react";
import { ThemeProvider, createTheme } from "@mui/material/styles";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Dashboard from "./components/Dashboard";
import NewPage from "./components/NewPage"; // Example of a new page

// Create a global theme for the application
const theme = createTheme({
  palette: {
    primary: {
      main: "#007bff", // Custom primary color for the theme
    },
    secondary: {
      main: "#f50057", // Custom secondary color
    },
  },
  typography: {
    fontFamily: "Roboto, Arial, sans-serif",
    h1: {
      fontSize: "2rem",
    },
    h2: {
      fontSize: "1.5rem",
    },
  },
});

function App() {
  return (
    <ThemeProvider theme={theme}>
      <Router>
        <Switch>
          <Route path="/dashboard" component={Dashboard} />
          <Route path="/new-page" component={NewPage} /> {/* New page */}
          <Route path="/" exact component={Dashboard} />
        </Switch>
      </Router>
    </ThemeProvider>
  );
}

export default App;
